package pageObject;import base.BasePage;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;public class RegisterSuccess extends BasePage {    public RegisterSuccess(WebDriver driver){        super(driver);    }    private static final By CONFIRM_MSG = By.cssSelector("div[id='content'] h1");    public String getConfirmMsg(){        try {            return elementUtils.getText(CONFIRM_MSG);        }catch (Exception e){            return e.getMessage();        }    }}