package utils;import org.testng.annotations.Test;import java.io.File;import java.io.FileInputStream;import java.io.IOException;import java.io.InputStream;import java.util.Properties;public class ConfigReader {    private Properties proper;    public ConfigReader(){        proper = new Properties();        try{            FileInputStream fi=new FileInputStream(System.getProperty("user.dir") + "/src/test/resources/config" +                    ".properties");            proper.load(fi);        }catch (IOException e){            throw new RuntimeException("failed to load config.properties file.");        }    }    public String getProperty(String key){        return proper.getProperty(key);    }}