package utils;import com.aventstack.extentreports.Status;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;public class Log {    private static final Logger logger = LogManager.getLogger("Automation.log");    public static void info(String message) {        logger.info(message);        if (ExtentManager.getTest() != null) ExtentManager.getTest().log(Status.INFO, message);    }    public static void warn(String message) {        logger.warn(message);        if (ExtentManager.getTest() != null) ExtentManager.getTest().log(Status.WARNING, message);    }    public static void error(String message) {        logger.error(message);        if (ExtentManager.getTest() != null) ExtentManager.getTest().log(Status.FAIL, message);    }    public static void attachScreenshot(String path) {        try {            if (ExtentManager.getTest() != null && path != null) {                ExtentManager.getTest().addScreenCaptureFromPath(path);            }        } catch (Exception e) {            logger.error("Attach screenshot failed: " + e.getMessage());        }    }}