package utils;import org.openqa.selenium.JavascriptExecutor;import org.openqa.selenium.OutputType;import org.openqa.selenium.TakesScreenshot;import org.openqa.selenium.WebDriver;import org.openqa.selenium.support.ui.WebDriverWait;import java.io.File;import java.io.IOException;import java.nio.file.Files;public class ScreenshotUtils {    public static String capture(WebDriver driver, String name){        try        {            File src = ((TakesScreenshot)driver).getScreenshotAs(OutputType.FILE);            String dir = "reports/screenshots/";            File targetDir = new File(dir);            if(!targetDir.exists()) targetDir.mkdirs();            String path = dir + name + "_" +System.currentTimeMillis()+".png";            File dest = new File(path);            Files.copy(src.toPath(), dest.toPath());            return path;        }catch (IOException e){            e.printStackTrace();            return null;        }    }}